CREATE TABLE sf_vkontakte_friendship (user_from BIGINT, user_to BIGINT, PRIMARY KEY(user_from, user_to)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE sf_vkontakte_user (id BIGINT AUTO_INCREMENT, settings BIGINT, fetched_at datetime, first_name TEXT, last_name TEXT, nickname TEXT, sex BIGINT, bdate TEXT, city BIGINT, country BIGINT, timezone TEXT, photo TEXT, photo_medium TEXT, photo_big TEXT, has_mobile TINYINT(1), rate FLOAT(18, 2), home_phone TEXT, mobile_phone TEXT, university BIGINT, university_name TEXT, faculty BIGINT, faculty_name TEXT, graduation BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
