CREATE TABLE friend_reference (user_from BIGINT, user_to BIGINT, PRIMARY KEY(user_from, user_to)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE user (id BIGINT AUTO_INCREMENT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, first_name TEXT, last_name TEXT, nickname TEXT, sex BIGINT, bdate TEXT, city BIGINT, country BIGINT, timezone TEXT, photo TEXT, photo_medium TEXT, photo_big TEXT, has_mobile TINYINT(1), rate FLOAT(18, 2), home_phone TEXT, mobile_phone TEXT, university BIGINT, university_name TEXT, faculty BIGINT, faculty_name TEXT, graduation BIGINT, settings BIGINT, fetched_at datetime, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
ALTER TABLE friend_reference ADD CONSTRAINT friend_reference_user_from_user_id FOREIGN KEY (user_from) REFERENCES user(id);
